import React, { useState } from 'react';
import '../css/App.css';
import CreateItem from '../components/CreateItem';
import Footer from "../components/Footer";
function App() {

  const [listOption, setlistOption] = useState([
    { title: 'Candidato 1', voted: false },
    { title: 'Candidato 2', voted: false },
    { title: 'Candidato 3', voted: false },
  ]);

  const [editTitle, setEditTitle] = useState({
    title: 'Double tap to edit question',
    editable: false
  });

  const handleVote = (index) => {
    setlistOption(
      listOption.map((vote, vindex) => {
        return vindex === index
          ? { ...vote, voted: !vote.voted }
          : vote
      }
      )
    )
  }

  const createOption = (value, e) => {
    e.preventDefault();
    if(!value)
      alert("Não é possível adicionar um opção vazia!")
    else
    setlistOption(
      listOption.concat({ title: value, voted: false })
    )
  }

  const removeOption = (index) => {
    setlistOption(
      listOption.filter((option, vindex) => { return index !== vindex })
    )
  }

  const handleEditTitle = (value) => {
    setEditTitle({ ...editTitle, title: value })
  }

  const toggleEditTitle = () => { 
    setEditTitle({ ...editTitle, editable: !editTitle.editable })
  }

  return (
    <>
    <h1 className="title" onDoubleClick={() => toggleEditTitle() }>
      { editTitle.editable === true 
      ? <><input className="inputTitle" onChange={(e) => handleEditTitle(e.target.value) }
       value={editTitle.title} placeholder="Edit title"></input></>
      : editTitle.title
      }
    </h1>
    <div className="container">
      <ul>
        {listOption.map((vote, index) =>
          <div className="subcontent">
            <li className="itemVote" onClick={() => handleVote(index)} key={index}>
              {vote.title}
            </li>
            <span onClick={() => removeOption(index)} className="voted">
              <img alt="" src="img/delete.png"></img>
            </span>
          </div>
        )}
        <CreateItem submitVote={createOption} />
      </ul>
    </div>
    <Footer></Footer>
    </>
  );
}

export default App;
