import React, { useState, useEffect } from 'react';
import '../css/App.css';
function App() {
  const [listVote, setListVote] = useState([
   { title: 'Candidato 1', voted: false },
   { title: 'Candidato 2', voted: false },
   { title: 'Candidato 3', voted: false },
  ]);

  const [InputValue, setInputValue] = useState('')


  const handleVote = (index) => {
    setListVote(
      listVote.map((vote, vindex) => { 
       return vindex === index 
        ? { ...vote, voted: !vote.voted }
        : vote
      }
      )
    )
  }

  const handleCreateVote = (value) => {
    setListVote(

      listVote.concat({ title: value, voted: false })
    )
  }

  return (
    <div className="container">
      <ul>
          { listVote.map((vote, index) => 
          <li className="itemVote" onClick={() => handleVote(index) } key={index}>
            { vote.title }
            <span className="voted">
              { vote.voted ? <img alt="" src="img/delete.png"></img> : null } 
            </span> 
          </li>
          )}
          <li className="createItem">
            <input className="inputCreate" onChange={(e) => { setInputValue(e.target.value) } }
             value={InputValue} placeholder="Insira novo voto"></input>
            <button className="buttonCreate" onClick={() => handleCreateVote(InputValue) }>
              <img alt="" src="img/plus.png"></img>
            </button>
          </li>
      </ul>
    </div>
  );
}

export default App;
