import React, { useState } from 'react';
import '../css/App.css';
function App() {
  const [listVote, setListVote] = useState([
   { title: 'Candidato 1', voted: false },
   { title: 'Candidato 2', voted: false },
   { title: 'Candidato 3', voted: false },
  ]);


  const handleVote = (index) => {
    setListVote(
      listVote.map((vote, vindex) => { 
        vote.voted === true 
        ? operationImg = 'img/delete.png'
        : operationImg = 'img/success.png'

       return vindex === index 
        ? { ...vote, voted: !vote.voted }
        : vote

      }
      )
    )
  }


  return (
    <div className="container">
      VOTE
      <ul>
          { listVote.map((vote, index) => 
          <li className="itemVote" onClick={() => handleVote(index) } key={index}>
            { vote.title }
            <span className="voted">
              { vote.voted ? <img src="img/delete.png"></img> : null } 
            </span> 
          </li>
          )}
          <li className="createItem">
            <input className="inputCreate" placeholder="Insira novo voto"></input>
            <button className="buttonCreate"><img alt="" src="img/plus"></img></button>
          </li>
      </ul>
    </div>
  );
}

export default App;
