import React, { useState } from 'react';
import '../css/App.css';
function App() {
  const [listVote, setListVote] = useState([
    'Candidato 1',
    'Candidato 2',
    'Candidato 3'
  ]);

  const handleVote = (index) => {
    setListVote(
      listVote.map((vote, vindex) => vindex === index ? { ...vote, voted: true } : vote)
    )
  }


  return (
    <div className="App">
      VOTE
      <ul>
          { listVote.map((vote, index) => 
          <li>
            {vote}<button onClick={() => handleVote(index) }>vote</button>
          </li>) }
      </ul>
    </div>
  );
}

export default App;
