import React, { useState } from 'react';
import '../css/App.css';
function App() {
  const [listVote, setListVote] = useState([
   { title: 'Candidato 1', voted: false },
   { title: 'Candidato 2', voted: false },
   { title: 'Candidato 3', voted: false },
  ]);

  const handleVote = (index) => {
    setListVote(
      listVote.map((vote, vindex) => vindex === index ? { ...vote, voted: !vote.voted } : vote)
    )
  }


  return (
    <div className="container">
      VOTE
      <ul>
          { listVote.map((vote, index) => 
          <li className="itemVote" onClick={() => handleVote(index) } key={index}>
            { vote.title }
            { vote.voted ? <span className="voted"><img src="img/success.png"></img></span> : null } 
          </li>
          )}
      </ul>
    </div>
  );
}

export default App;
